var flowDate = flow.get("neueZeit_Startzeit") || 0;
var greaterEqual = false;
var lessThan = false;

if (flowDate != 0) {
    var currentDate = new Date();  
    var currentTimeAsString = currentDate.getHours() + currentDate.getMinutes() + currentDate.getSeconds();
    var flowTimeAsString = flowDate.getHours() + flowDate.getMinutes() + flowDate.getSeconds();

    if (flowTimeAsString >= currentTimeAsString) {
        greaterEqual = true;
    } else if (flowTimeAsString < currentTimeAsString) {
        lessThan = true;
    }
}

return [{payload: greaterEqual}, {payload: lessThan}];
